scriptlet.script:
protocol = scriptletContext.get("protocol");
error = scriptletRawResult['exception'];
result = scriptletRawResult['Result'];
returnCode = scriptletRawResult['returnCode'];
out = scriptletRawResult['stdOut'];
err = scriptletRawResult['stdErr'];
if (returnCode == -1)
{
    if ((error == null) || (error == ""))
        error = "Could not get information. " + err;

    scriptletContext.put("stdErr", error);
}
else if ((protocol.indexOf("Telnet") != -1) && (error == null))
{
    if ((out == null) || (out ==""))
    {
         scriptletContext.put("returnCode", "-1");
         scriptletContext.put("stdErr", "Could not get information");    
    }
    if (result.toLowerCase().indexOf("file or directory in the path name does not exist") != -1)
    {
         scriptletContext.put("returnCode", "-1");
         scriptletContext.put("stdErr", result);
    }
    if (result.toLowerCase().indexOf("file access permissions do not allow the specified action") != -1)
    {
         scriptletContext.put("returnCode", "-1");
         scriptletContext.put("stdErr", result);      
    }  
    if (result.toLowerCase().indexOf("not a recognized flag") != -1)
    {
         scriptletContext.put("returnCode", "-1");
         scriptletContext.put("stdErr", result);      
    }
    if (result.toLowerCase().indexOf("not found") != -1)
    {
         scriptletContext.put("returnCode", "-1");
         scriptletContext.put("stdErr", result);      
    }
    if (result.toLowerCase().indexOf("bad number of packets to transmit") != -1)
    {
         scriptletContext.put("returnCode", "-1");
         scriptletContext.put("stdErr", result);      
    }
    if (result.toLowerCase().indexOf("illegal packet size") != -1)
    {
         scriptletContext.put("returnCode", "-1");
         scriptletContext.put("stdErr", result);      
    }
    if (result.toLowerCase().indexOf("permission denied") != -1)
      {
          scriptletContext.put("returnCode", "-1");
          scriptletContext.put("stdErr", result);   
      }
      if (result.toLowerCase().indexOf("usage") != -1)
      {
          scriptletContext.put("returnCode", "-1");
          scriptletContext.put("stdErr", result);   
      }
      if (result.toLowerCase().indexOf("not found") != -1)
      {
          scriptletContext.put("returnCode", "-1");
          scriptletContext.put("stdErr", result);   
      }
      if (result.toLowerCase().indexOf("100% packet loss") != -1)
      {
          scriptletContext.put("returnCode", "-1");
          scriptletContext.put("stdErr", result);   
      }
}
