scriptlet.script:
protocol = scriptletContext.get("protocol");
error = scriptletRawResult['exception'];
result = scriptletRawResult['Result'];
returnCode = scriptletRawResult['returnCode'];
stdErr = scriptletRawResult['stdErr'];
if (returnCode == -1)
{
    if ((error == null) || (error == ""))
    {
        if ((stdErr == "" ) || (stdErr == null))
            error = "Could not kill process";
        else  
           error = stdErr;
     }
     scriptletContext.put("Error String", error);
}
else if ((protocol.indexOf("Telnet") != -1) && (error == null))
{
    if (result.toLowerCase().indexOf("arguments must be %job or process ids") != -1)
    {
         scriptletContext.put("Code", "-1");
         scriptletContext.put("Error String", result);
     }
     if (result.toLowerCase().indexOf("no such process") != -1)
      {
         scriptletContext.put("Code", "-1");
         scriptletContext.put("Error String", result);      
      }
      if (result.toLowerCase().indexOf("bad option(s)") != -1)
      {
          scripletContext.put("Code", "-1");
          scriptletContext.put("Error String", result);   
      }
      if (result.toLowerCase().indexOf("not found") != -1)
      {
          scriptletContext.put("Code", "-1");
          scriptletContext.put("Error String", result);         
      }
      if (result.toLowerCase().indexOf("cannot execute") != -1)
      {
          scriptletContext.put("Code", "-1");
          scriptletContext.put("Error String", result);         
      }
       if (result.toLowerCase().indexOf("permission denied") != -1)
      {
          scriptletContext.put("Code", "-1");
          scriptletContext.put("Error String", result);         
      }
       if (result.toLowerCase().indexOf("invalid id") != -1)
      {
          scriptletContext.put("Code", "-1");
          scriptletContext.put("Error String", result);         
      }      

}
