scriptlet.script:
var command = "ping";
var exception = "";

var size = getInputValue("packetSize");
if (size != null)
	command += " -l " + size;

var count = getInputValue("packetCount");
if (count != null) 
	command += " -n " + count;
/*
var timeout = getInputValue("timeout");
if (timeout != null)
	command += " -w " + timeout;
*/
var remoteOS = getInputValue("remoteOS");
if (remoteOS == null) 
	exception = "Did not receive a valid input for 'remoteOS'. It is a required value for 'WMI' commands";
else if (remoteOS != "WINDOWS" && remoteOS != "LINUX" && remoteOS != "SOLARIS")
    exception = "remoteOS must have one of the following values: [WINDOWS, LINUX, SOLARIS]";//SUNOS, HPUX, AIX, MAC_OS

if (exception == "") {
	var targetHost = getInputValue("targetHost");
	if (targetHost != null)
		command += " " + targetHost;
	else
		exception = "targetHost is a required parameter for the ping. Please verify that targetHost is specified in the arguments passed in.";
}

if (exception == "") {
	scriptletContext.put("command", command);
} else {
	scriptletContext.put("exception", exception);
	scriptletContext.put("returnResult", exception);
}

function getInputValue(inputName) {
    if ((typeof(scriptletContext.get(inputName)) != "undefined") && (scriptletContext.get(inputName) != ""))
        return scriptletContext.get(inputName);
    else
        return null;
}

var output = getInputValue("stdOut");
var returnCode = getInputValue("returnCode");

if(returnCode == 0 && output != null) {
	scriptletContext.put("packetsSent",			 extractValue(output, "Sent = ",		","));
	scriptletContext.put("packetsReceived",		 extractValue(output, "Received = ",	","));
	scriptletContext.put("percentagePacketsLost",extractValue(output, " (",		"% loss)"));
	scriptletContext.put("transmissionTimeMin",	 extractValue(output, "Minimum = ",	"ms"));
	scriptletContext.put("transmissionTimeMax",	 extractValue(output, "Maximum = ",	"ms"));
	scriptletContext.put("transmissionTimeAvg",	 extractValue(output, "Average = ",	"ms"));
} else {
	scriptletContext.put("packetsSent",			 "");
	scriptletContext.put("packetsReceived",		 "");
	scriptletContext.put("percentagePacketsLost","");
	scriptletContext.put("transmissionTimeMin",	 "");
	scriptletContext.put("transmissionTimeMax",	 "");
	scriptletContext.put("transmissionTimeAvg",	 "");
}

function extractValue(output, key, endTag) {
	var value = "";
	var startIndex = -1;
	var valueStartIndex = -1;
	var endIndex = -1;

	startIndex = output.indexOf(key);
	valueStartIndex = startIndex + key.length;
	endIndex = output.indexOf(endTag, valueStartIndex);
	if(startIndex >= 0 && endIndex >= 0) {
		value = output.substring(valueStartIndex, endIndex);
	}
	return value;
}

function getInputValue(inputName) {
	if ((typeof(scriptletContext.get(inputName)) != "undefined") && (scriptletContext.get(inputName) != "")) {
		return scriptletContext.get(inputName);
	} else {
		return null;
	}
}
