scriptlet.script:
protocol = scriptletContext.get("protocol");
error = scriptletRawResult['exception'];
result = scriptletRawResult['Result'];
returnCode = scriptletRawResult['returnCode'];
out = scriptletRawResult['stdOut'];
err = scriptletRawResult['stdErr'];
if (returnCode == -1)
{
    if ((error == null) || (error == ""))
        error = "Could not get information. " + err;

    scriptletContext.put("Error String", error);
}
else if ((protocol.indexOf("Telnet") != -1) && (error == null))
{
    if ((out == null) || (out ==""))
    {
         scriptletContext.put("Code", "-1");
         scriptletContext.put("Error String", "Could not get information");    
    }
    if (result.toLowerCase().indexOf("not found") != -1)
    {
         scriptletContext.put("Code", "-1");
         scriptletContext.put("Error String", result);
     }
     if (result.toLowerCase().indexOf("unknown host") != -1)
      {
         scriptletContext.put("Code", "-1");
         scriptletContext.put("Error String", result);      
      }
      if (result.toLowerCase().indexOf("bad data size") != -1)
      {
         scriptletContext.put("Code", "-1");
         scriptletContext.put("Error String", result);      
      }
      if (result.toLowerCase().indexOf("data size too large") != -1)
      {
         scriptletContext.put("Code", "-1");
         scriptletContext.put("Error String", result);      
      }
      if (result.toLowerCase().indexOf("permission denied") != -1)
      {
         scriptletContext.put("Code", "-1");
         scriptletContext.put("Error String", result);      
      }
      if (result.toLowerCase().indexOf("cannot execute") != -1)
      {
         scriptletContext.put("Code", "-1");
         scriptletContext.put("Error String", result);      
      }  
      if (result.toLowerCase().indexOf("no answer from") != -1)
      {
          scriptletContext.put("Code", "-1");
          scriptletContext.put("Error String", result);
      }
}

scriptletResult="";
var parts = scriptletInput.split(/=/);
if(parts.length > 1){
var names = parts[0].split(/\//);
var vals = parts[1].split(/\//);
for (count = 0; count < names.length; count++){
	curr = names[count].replace(/\s$/, "").replace(/^\s/, "").toLowerCase();
	if (curr == "stddev" || curr == "standard deviation" ||  curr == "deviation" || curr=="mdev"){
		scriptletResult=vals[count];
	break;
	}
}
}

