scriptlet.script:
scriptletResult = scriptletInput
n = scriptletResult.lastIndexOf(".");
if (n >= 0)
{
    number = scriptletResult.substr(n+1);
    scriptletResult = scriptletResult.substring(0, n);
    n = scriptletResult.lastIndexOf(".");
    scriptletResult = scriptletResult.substring(0, n)+number+scriptletResult.substr(n);
}

scriptletResult = scriptletInput;
n = scriptletResult.lastIndexOf("\\");
if (n >= 0)
    scriptletResult = scriptletResult.substr(n + 1);
