scriptlet.script:
//# deal with escaped chars
if (colsep == "\\t") {
  colsep = "\t";
}
if (colsep == "\\w") {
  colsep = " ";
}
if (rowsep == "\\n") {
  rowsep = "\n";
}
if (rowsep == "\\r") {
  rowsep = "\r";
}
if (esctable == "true") {
  tabledata = tabledata.replace("\\\r", "\r");
  tabledata = tabledata.replace("\\\n", "\n");
  tabledata = tabledata.replace("\\\t", "\t");
}

//# globals
h = new java.util.HashMap(); //# use hash because sparse arrays have problems
table = tabledata.split(rowsep);
var cells = new Array();

for(i = 0; i < table.length; i++)
{
    cell = table[i].split(colsep);
    cells.push(cell);
}

cells.sort();

var k = 0;

for(i = 0; i < cells.length; i++){
    positions = h.get(cells[i]);
    if(!positions){
        positions = new Array();
        positions[0]=k;
    }
    else{
        positions.push(k);
    }
    h.put(cells[i],positions);
    k++;
}

var k = 0;
var newtable = new Array();

for(i = 0; i < table.length; i++){
    cell = table[i].split(colsep);
    positions = h.get(cells[i]);
    k = positions.pop();
    newtable[k] = cells[i];
}

var newTableString = "";

if((maxrow<0) || (maxrow > newtable.length)){
    maxrow = newtable.length;
}

var threshold = scriptletContext.get("threshold");

for (i = minrow; i < maxrow; i++) {
 /* 
  #
  # check if we've passed the threshold
  #
 */
  if (threshold != null) {
    if (order == "asc") {
      //# alpha thresh
      if (type == "alpha") {
        if (newtable[i] > threshold) {
          break;
        }
      }
      //# numeric thresh
      else if((type == "int") || (type == "float")) {
        if (newtable[i] > threshold) {
          break;
        }
      }
    }
    if (order == "desc") {
      //# alpha thresh
      if (type == "alpha") {
        if (newtable[i] < threshold) {
          break;
        }
      }
      //# numeric thresh
      else if((type == "int") || (type == "float")) {
        if (newtable[i] > threshold) {
          break;
        }
      }
    }
  }
/*
  #
  # assemble the line in requested order: "asc" | "desc"
  #
 */
  if (i == minrow) {
    newTableString = newtable[i];
  }
  else {
    if (order == "asc") {
      newTableString = newTableString + rowsep + newtable[i];
    }
    if (order == "desc") {
      newTableString = newtable[i] + rowsep + newTableString;
    }
  }
}
//# STORE RESULTS
scriptletResult = newTableString;
scriptletResponse = "success";
