scriptlet.script:
var variable = getValue(keyName);
var trimSpaces = getBooleanValue("trimSpaces");

if (trimSpaces && (variable == null || trim(variable) == "")) {
    scriptletResponse = "isNull";
} else if (!trimSpaces && variable == null) {
    scriptletResponse = "isNull";
} else {
    scriptletResponse = "notNull";
} 

if (variable != null) {
	scriptletResult = trim(variable); 
} else {
	scriptletResult = ""; 
}

// ---------- helper functions ----------
function trim(value) {
    var s = "" + value;
    return s.replace(/^\s*|\s*$/g, ""); 
}

function getValue(inputName) {
    var inputValue = scriptletContext.get(inputName);
    if (inputValue != null && inputValue.length() > 0) {
        return inputValue;
    }
    return null;
}

function getBooleanValue(inputName) {
    var inputValue = scriptletContext.get(inputName);
    if (inputValue != null && inputValue.length() > 0 && inputValue.toLowerCase() == "true") {
        return true;
    }
    return false;
}
