scriptlet.script:
var keys = keyNames.split(delimiter);
var vals = myValues.split(delimiter);
scriptletResult = myValues;
var oneval = vals == null || vals.length < keys.length;
for (count = 0;count < keys.length;count++){
	if (oneval)
		scriptletContext.put(keys[count], myValues);
	else
		scriptletContext.put(keys[count], vals[count]);		
}
